# coding: utf-8

n = int(input("Введите количество знаков после запятой, с которыми вывести число пи\n"))
if n < 0:
    print("Введите неотрицательное число!")
elif n < 17:
    import math
    print("Число пи с {n} знаками после запятой: {pi:.{n}f}".format(n=n, pi=math.pi))
elif n < 1001:
    pi = """3."""
    print("Число пи с {n} знаками после запятой: {pi}".format(n=n, pi=pi[:n+2]))
elif n < 4000001:
    print("Для получения {n} знаков после запятой придётся сделать запрос на сайт sanstv.ru".format(n=n))
    import requests
    t = requests.get("https://sanstv.ru/pi")
    if t.status_code == 200:
        print("Число пи с {n} знаками после запятой: {pi}".format(n=n, pi=t.text.replace("\n","")[t.text.replace("\n","").find("3."):t.text.replace("\n","").find("3.")+n+2]))
    else:
        print("Ошибка соединения")
else:
    print("{n} знаков слишком много!\nЧеловечеству уже известно 10 триллионов цифр после запятой в числе пи.\nПоищите их в других источниках.".format(n=n))
